import React, {useEffect, useState} from 'react';
import './style/Chat.css';
import Axios from 'axios';

function Chat(props) {
    const message = React.createRef();
    const alias = React.createRef();
    const password = React.createRef();

    const sendMessage = (e) => {
        Axios.post('http://localhost:3001/api/insert', {
            alias: alias, 
            message: message
        }).then(() => {
            alert("seccessful insert!");
        });
    };

    const createUser = () => {

    };

    const userLogin = () => {

    };

    return(
        <div className='Container'>
            <div className='SideBar ContainerItem'>
                <div className='AccessGranted'> &gt; Access Granted!</div>
                <div className='Bar'></div>
                <div className='LoginAlias'>Login with your previously created alias.</div>
                <form className='AliasForm' onSubmit={userLogin}>
                    <input className='AliasInput' type='text' placeholder='Alias' ref={alias}/>
                    <input className='AliasPassword' type='password' placeholder='Password' ref={password}/>
                    <button className='LoginButton'>Login</button>
                </form>
                <div className='Bar'></div>
                <div>Or</div>
                <div className='Bar'></div>
                <div className='CreateAlias'>Create new alias.</div>
                <form className='AliasForm' onSubmit={createUser}> 
                    <input className='AliasInput' type='text' placeholder='Alias' ref={alias}/>
                    <input className='AliasPassword' type='password' placeholder='Password' ref={password}/>
                    <button className='LoginButton'>Create</button>
                </form>
                <div className='Bar'></div>
                <div className='CurrentAlias'>Current Alias: <span className='Alias'>{alias.current}</span></div>
                <div className='SideVersion'>Version:1.1.4</div>
            </div>
            <div className='ChatBox ContainerItem'>
                <div className='MessageBox'>
                    &lt;<span className='Alias'>Peter</span>&gt; Hello!
                </div>
                <form className='MessageForm' onSubmit={sendMessage}>
                    <input className='MessageInput' type='text' placeholder='Message' ref={message}/>
                    <button className='SendButton'>Send</button>
                </form>
            </div>
        </div>
    );
}

export default Chat;
