import React, {useEffect, useState} from 'react';
import './style/Chat.css';
import Axios from 'axios';

function Chat() {
    const [message, setMessage] = useState('');
    const [alias, setAlias] = useState('(None)');
    const [displayAlias, setDisplayAlias] = useState('(None)');
    const [password, setPassword] = useState('');

    const sendMessage = (e) => {
        setMessage(e.target.value);

        Axios.post('http://localhost:3001/api/insert', {
            alias: alias, 
            message: message
        }).then(() => {
            alert("seccessful insert!");
        });
    };

    const login = (e) => {
        setAlias(e.target.value);
    };

    const createAlias = (e) => {
        setPassword(e.target.value);
    }

    const createUser = () => {
        setDisplayAlias(Alias);
    };

    return(
        <div className='Container'>
            <div className='SideBar ContainerItem'>
                <div className='AccessGranted'> &gt; Access Granted!</div>
                <div className='Bar'></div>
                <div className='LoginAlias'>Login with your previously created alias.</div>
                <form className='AliasForm'>
                    <input className='AliasInput' type='text' placeholder='Alias' onChange={(e) => {
                        setAlias(e.target.value);
                    }}/>
                    <input className='AliasPassword' type='password' placeholder='Password' onChange={(e) => {
                        setPassword(e.target.value);
                    }}/>
                    <button className='LoginButton'>Login</button>
                </form>
                <div className='Bar'></div>
                <div>Or</div>
                <div className='Bar'></div>
                <div className='CreateAlias'>Create new alias.</div>
                <form className='AliasForm' onSubmit={createUser}> 
                    <input className='AliasInput' type='text' placeholder='Alias' onChange={(e) => {
                        setAlias(e.target.value);
                    }}/>
                    <input className='AliasPassword' type='password' placeholder='Password' onChange={(e) => {
                        setPassword(e.target.value);
                    }}/>
                    <button className='LoginButton'>Create</button>
                </form>
                <div className='Bar'></div>
                <div className='CurrentAlias'>Current Alias: <span className='Alias'>{alias}</span></div>
                <div className='SideVersion'>Version:1.1.4</div>
            </div>
            <div className='ChatBox ContainerItem'>
                <div className='MessageBox'>
                    &lt;<span className='Alias'>Peter</span>&gt; Hello!
                </div>
                <form className='MessageForm' onSubmit={sendMessage}>
                    <input className='MessageInput' type='text' placeholder='Message'/>
                    <button className='SendButton'>Send</button>
                </form>
            </div>
        </div>
    );
}

export default Chat;
