import React, {useEffect, useState} from 'react';
import './App.css';

function AuthorizationPage(props) {
  let accessCode = Math.floor(Math.random()*10000000000);
  let accessCodeInput = React.createRef();
  const [height, width] = useWindowSize();

  function handleSubmit (event) {
    event.preventDefault();
    if(parseInt(accessCodeInput.current.value) === accessCode){
      props.setAccess(true);
    }
  }

  if(height > 400 && width > 445){
    return (
      <div className='AuthorizationPage'>
        <div className='CenterContainer'>
          <div className='Welcome'>This <em>Portfolio</em> is Restricted.</div>
          <div className='Hint'>(Not really though... the access code is hidden somewhere in this website)</div>       
          <form className='AccessCode' onSubmit={handleSubmit}>
            <input type='text' placeholder='Access Code' ref={accessCodeInput}/>
            <button>Enter</button>
          </form>
        </div>
        <div className='HiddenCode'>{accessCode}</div>
        <div className='Version'>Version:1.1.4 - Updated:6/11/20</div>
      </div>
    )
  }
  else {
    return (
      <div className='AuthorizationPage'>
        <div className='CenterContainer'>
          <div className='WelcomeMobile'>This <em>Portfolio</em> is Restricted.</div>
          <div className='Hint'>(Not really though... the access code is hidden somewhere in this website)</div>       
          <form className='AccessCode' onSubmit={handleSubmit}>
            <input type='text' placeholder='Access Code' ref={accessCodeInput}/>
            <button>Enter</button>
          </form>
        </div>
        <div className='HiddenCode'>{accessCode}</div>
        <div className='Version'>Version:1.1.4 - Updated:6/11/20</div>
      </div>
    )
  }
}

function PortfolioPage() {
  return (
    <div className='PortfolioPage'>

      <div className='Congrats'> Access Granted. Congrats!</div>

      <div className='Resume'>

        <div className='Heading'>
          <div>
            <h1>Peter Glass</h1>
            <p>peter.glass@utdallas.edu, peterglass123@gmail.com</p>
          </div>
          <p className='PhoneNumber'>(737) 402-9521</p>
        </div>

        <div className='Section'>
          <h2>Education</h2>
          <p>
            <b>Bachelor of Science in Computer Science (GPA: 3.86/4.00)</b><br/> 
            The University of Texas at Dallas, Richardson, Texas<br/>
            Anticipated May 2021<br/>
            <br/>
            <b>Undeclared, Core Courses (GPA: 3.38/4.00)</b><br/>
            The University of Texas at Austin, Austin, Texas<br/>
            Sep 2017 – Dec 2018<br/>
          </p>
        </div>

        <div className='Projects'>
          <div>
            <h2>Projects</h2>
            <p>
              <b>Lambda Fuel Map (FSAE Club Project)</b><br/>
              Sep 2019 – Dec 2019 
            </p>
            <ul>
              <li>Java Swing based application that maps lambda values from a log file to a table and then colors the cells based on the cell value</li>
              <li>Worked in a group of four using an agile form of collaboration and GitHub <a href='https://github.com/DallasFormulaRacing/DataAnalyzer' target='_blank' rel="noopener noreferrer">(Repository Link)</a></li>
            </ul>
          </div>
          <img className='LambdaTable' src='Lambda_Table.PNG' alt='Lambda Table' width='350' height='200' rel="noopener noreferrer"></img>
        </div>

        <div className='Section'>
          <h2>Current Courses</h2>
          <ul>
            <li>Database Systems</li>
