import React, {useState} from 'react';
import './App.css';

function AccessCode(props) {
  let accessCodeInput = React.createRef();

  function handleSubmit (event) {
    event.preventDefault();
    if(accessCodeInput.current.value == props.code){
      window.alert("access granted.");
      setAccess(true);
    }
  }

  return (
    <form className='AccessCode' onSubmit={handleSubmit}>
      <input type='text' placeholder='Access Code' ref={accessCodeInput}/>
      <button>Enter</button>
    </form>
  )
}

function HiddenCode(props) {
  return (
    <div className='HiddenCode'>
      {props.code}
    </div>
  )
}

function App() {
  let randomNum = Math.floor(Math.random()*10000000000);
  const [hasAccess, setAccess] = useState(false);

  return (
    <div className='App'>
      <div className='CenterContainer'>
        <div className='Welcome'>This Portfolio is Restricted.</div>
        <div className='Hint'>(Not really though... the access code is hidden somewhere in this website)</div>       
        <AccessCode code={randomNum} />
      </div>
      <div className={HiddenCode}>{randomNum}</div>
      <div className='Version'>Version:1.1.3 - Updated:6/11/20</div>
    </div>
  );
}

export default App;
