import React, {useState} from 'react';
import './App.css';

function AuthorizationPage(props) {
  let accessCode = Math.floor(Math.random()*10000000000);
  let accessCodeInput = React.createRef();

  function handleSubmit (event) {
    event.preventDefault();
    if(parseInt(accessCodeInput.current.value) === accessCode){
      window.alert("access granted.");
      props.setAccess(true);
    }
    console.log(props.hasAccess);
  }

  return (
    <div className='AuthorizationPage'>
      <div className='CenterContainer'>
        <div className='Welcome'>This Portfolio is Restricted.</div>
        <div className='Hint'>(Not really though... the access code is hidden somewhere in this website)</div>       
        <form className='AccessCode' onSubmit={handleSubmit}>
          <input type='number' placeholder='Access Code' ref={accessCodeInput}/>
          <button>Enter</button>
        </form>
      </div>
      <div className='HiddenCode'>{accessCode}</div>
      <div className='Version'>Version:1.1.3 - Updated:6/11/20</div>
    </div>
  )
}

function PortfolioPage() {
  return (
    <div>access granted</div>
  )
}

function App() {
  let [hasAccess, setAccess] = useState(false);

  return (
    <div className='App'>
      {!hasAccess && <AuthorizationPage hasAccess={hasAccess} setAccess={setAccess} /> }
      {hasAccess && <PortfolioPage />}
    </div>
  );
}

export default App;
