import React, {useEffect, useState} from 'react';
import './style/Chat.css';
import Axios from 'axios';

function Chat(props) {
    const [alias, setAlias] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [displayAlias, setDisplayAlias] = useState('(None)');
    const [messageLog, setMessageLog] = useState([]);
    const [isNewUser, setIsNewUser] = useState(false);

    useEffect(() => {
        getMessageLog();
        setInterval(getMessageLog, 500);
    }, []);

    const getMessageLog = () => {
        Axios.get("http://localhost:3001/api/get").then((response) => {
            setMessageLog(response.data);
        });
    };

    const sendMessage = (e) => {
        e.preventDefault();
        Axios.post('http://localhost:3001/api/insert', {
            alias: alias, 
            message: message
        });
    };

    const createUser = (e) => {
        e.preventDefault();
        setDisplayAlias(alias);
    };

    const userLogin = (e) => {
        e.preventDefault();
        setDisplayAlias(alias);
    };

    const openCreateAlias = () => {
        setIsNewUser(true);
    };

    return(
        <div>
            {!isNewUser && <div className='PopBox'>
                <div className='LoginMessage'>Use previously created alias and password to login. (Passwords cannot be recovered). </div>
                <form className='LoginForm'>
                    <input className='AliasInput' type='text' placeholder='Alias'/><br/>
                    <input className='PasswordInput' type='password' placeholder='Password'/><br/>
                    <button>Login</button>
                </form><br/>
                <button className="CreateAlias" onClick={openCreateAlias}>Create new alias.</button>
            </div>}
            {isNewUser && <div className='PopBox'>
            <div className='LoginMessage'>Create new alias.</div>
                <form className='LoginForm'>
                    <input className='AliasInput' type='text' placeholder='Alias'/><br/>
                    <input className='PasswordInput' type='password' placeholder='Password'/><br/>
                    <button>Create Alias</button>
                </form><br/>
                <button className="CreateAlias" onClick={openCreateAlias}>Create new alias.</button>
            </div>}
            <div className='FlexContainer'>
                <div className='ChatBox ContainerItem'>
                    <div className='ChatHeader'>
                        <div className='OnlineUsers'>
                            <span className='OnlineBullet'>&#8226;</span> Active Users: 7
                        </div>
                        <div className='CurrentAlias'>Current Alias: <span className='Alias'>peter</span></div>
                    </div>
                    <div className='MessageBox'>
                        {messageLog.map((val) => {
                            return <div>&lt;<span className='Alias'>{val.alias}</span>&gt; {val.message}</div>
                        })}
                    </div>
                
                    <form className='MessageForm' onSubmit={sendMessage}>
                        <input className='MessageInput' required type='text' placeholder='Message' onChange={(e) => {
                            setMessage(e.target.value);
                        }}/>
                        <button className='SendButton'>Send</button>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Chat;
