import React, {useEffect, useState} from 'react';
import './style/Chat.css';
import Axios from 'axios';

function Chat(props) {
    const [alias, setAlias] = useState('(None)');
    const [tempAlias, setTempAlias] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [messageLog, setMessageLog] = useState([]);
    const [isNewUser, setIsNewUser] = useState(false);
    const [isLogin, setIsLogin] = useState(false);
    const [hasError, setHasError] = useState(false);
    const [errorMessage, setErrorMessage] = useState('test');

    useEffect(() => {
        getMessageLog();
        setInterval(getMessageLog, 500);
    }, []);

    const getMessageLog = () => {
        Axios.get("http://localhost:3001/api/getMessage").then((response) => {
            setMessageLog(response.data);
        });
    };

    const sendMessage = (e) => {
        e.preventDefault();
        Axios.post('http://localhost:3001/api/insertMessage', {
            alias: alias, 
            message: message
        });
    };

    const createUser = (e) => {
        e.preventDefault();
        setAlias(tempAlias);
        setIsLogin(true);
        Axios.post("http://localhost:3001/api/insertUser", {
            alias: tempAlias,
            password: password
        });
    };

    const userLogin = (e) => {
        e.preventDefault();
        Axios.get("http://localhost:3001/api/isUserAuth", {
            params: {
                alias: tempAlias,
                password: password
            }
        }).then((response) => {
            if(response.data){
                setAlias(tempAlias);
                setIsLogin(true);
            } else {
                //setErrorMessage('Account credentials are incorrect.');
                setHasError(true);
            }
        });
    };

    const openCreateAlias = () => {
        setIsNewUser(true);
        setHasError(false);
    };

    const openLogin = () => {
        setIsNewUser(false);
        setHasError(false);
    };

    const logout = () => {
        setIsLogin(false);
        setAlias("(None)")
    };

    let flexClass;
    if(isLogin){
        flexClass = 'FlexContainer';
    } else {
        flexClass = 'BlurFlexContainer';
    }

    if(!hasError){
        //setErrorMessage('');
    }

    return(
        <div>
            {(!isNewUser && !isLogin) && <div className='PopBox'>
                <div className='PopMessage'>Use previously created alias and password to login. (Passwords cannot be recovered). </div>
                <form className='PopForm' onSubmit={userLogin}>
                    <input className='AliasInput' required type='text' placeholder='Alias' onChange={(e) => {
                        setTempAlias(e.target.value);
                    }}/><br/>
                    <input className='PasswordInput' type='password' placeholder='Password' onChange={(e) => {
                        setPassword(e.target.value);
                    }}/><br/>
                    <button>Login</button>
                </form><br/>
                <button className="PopSwitch" required onClick={openCreateAlias}>Create new alias.</button>
                <div className='Error'>{errorMessage}</div>
            </div>}

            {(isNewUser && !isLogin) && <div className='PopBox'>
            <div className='PopMessage'>Create new alias. Bad passwords will be shamed. (Passwords cannot be recovered).</div>
                <form className='PopForm' onSubmit={createUser}>
                    <input className='AliasInput' required type='text' placeholder='Unique Alias' onChange={(e) => {
                        setTempAlias(e.target.value);
                    }}/><br/>
                    <input className='PasswordInput' required type='password' placeholder='Create Password' onChange={(e) => {
                        setPassword(e.target.value);
                    }}/><br/>
                    <button>Create Alias</button>
                </form><br/>
                <button className="PopSwitch" onClick={openLogin}>Login with alias.</button>
                <div className='Error'>{errorMessage}</div>
            </div>}

            <div className={flexClass}>
                <div className='ChatBox ContainerItem'>
                    <div className='ChatHeader'>
                        <div className='OnlineUsers'>
                            <span className='OnlineBullet'>&#8226;</span> Active Users: 7
                        </div>
                        <div className='CurrentAlias'>Current Alias: <span className='Alias'>{alias}</span> | </div>
                        <button className='LogoutButton' onClick={logout}>Logout</button>
                    </div>
                    <div className='MessageBox'>
                        {messageLog.map((val) => {
                            return <div>&lt;<span className='Alias'>{val.alias}</span>&gt; {val.message}</div>
                        })}
                    </div>
                
                    <form className='MessageForm' onSubmit={sendMessage}>
                        <input className='MessageInput' required type='text' placeholder='Message' onChange={(e) => {
                            setMessage(e.target.value);
                        }}/>
                        <button className='SendButton'>Send</button>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Chat;
