import React, {useEffect, useState} from 'react';
import './style/Chat.css';
import Axios from 'axios';

function Chat(props) {
    const [alias, setAlias] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [displayAlias, setDisplayAlias] = useState('(None)');
    const [messageLog, setMessageLog] = useState([]);

    useEffect(() => {
        getMessageLog();
        setInterval(getMessageLog, 500);
    }, []);

    const getMessageLog = () => {
        Axios.get("http://localhost:3001/api/get").then((response) => {
            setMessageLog(response.data);
        });
    };

    const sendMessage = (e) => {
        e.preventDefault();
        Axios.post('http://localhost:3001/api/insert', {
            alias: alias, 
            message: message
        });
    };

    const createUser = (e) => {
        e.preventDefault();
        setDisplayAlias(alias);
    };

    const userLogin = (e) => {
        e.preventDefault();
        setDisplayAlias(alias);
    };

    return(
        <div className='Container'>
            <div className='ChatBox ContainerItem'>
                <div className='OnlineUsers'>
                    <span className='OnlineBullet'>â€¢</span> Active Users: 7
                </div>
                <div className='MessageBox'>
                    {messageLog.map((val) => {
                        return <div>&lt;<span className='Alias'>{val.alias}</span>&gt; {val.message}</div>
                    })}
                </div>
                
                <form className='MessageForm' onSubmit={sendMessage}>
                    <input className='MessageInput' required type='text' placeholder='Message' onChange={(e) => {
                        setMessage(e.target.value);
                    }}/>
                    <button className='SendButton'>Send</button>
                </form>

            </div>
        </div>
    );
}

export default Chat;
