import React from 'react';
import logo from './logo.svg';
import './App.css';

function HiddenCode(props) {
  return (
    <pre className='HiddenCode'>
      {props.code}
    </pre>
  )
}

function Welcome() {
  return (
    <div className='Welcome'>
      Welcome to the game.
    </div>
  )
}

function AccessCode(props) {
  let accessCodeInput = React.createRef();

  function handleSubmit (event) {
    event.preventDefault();
    if(accessCodeInput.current.value == props.code){
      window.prompt("access granted.");
    }
  }

  return (
    <form className='AccessCode' onSubmit={handleSubmit}>
      <input type='text' placeholder='Access Code' ref={accessCodeInput} required/>
      <button>Enter</button>
    </form>
  )
}

function App() {
  let randomNum = Math.floor(Math.random()*100000000);

  return (
    <div>
      <HiddenCode code={randomNum}/>
      <div className='Welcome'>This Website is Restricted.</div>
      <AccessCode code={randomNum} />
    </div>
  );
}

export default App;
