import React, {useEffect, useState} from 'react';
import './style/Chat.css';
import Axios from 'axios';

function Chat(props) {
    const [alias, setAlias] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [displayAlias, setDisplayAlias] = useState('(None)');

    useEffect(() => {
        console.log(password);
      }, [password]);

    const sendMessage = (e) => {
        e.preventDefault();
        Axios.post('http://localhost:3001/api/insert', {
            alias: alias, 
            message: message
        }).then(() => {
            alert("seccessful insert!");
        });
    };

    const createUser = (e) => {
        e.preventDefault();
        setDisplayAlias(alias);
    };

    const userLogin = (e) => {
        e.preventDefault();
        setDisplayAlias(alias);
    };

    return(
        <div className='Container'>
            <div className='SideBar ContainerItem'>
                <div className='AccessGranted'> &gt; Access Granted!</div>
                <div className='Bar'></div>
                <div className='LoginAlias'>Login with your previously created alias.</div>

                <form className='AliasForm' onSubmit={userLogin}>
                    <input className='AliasInput' required type='text' placeholder='Alias' onChange={(e) => {
                        setAlias(e.target.value);
                    }}/>
                    <input className='AliasPassword' required type='password' placeholder='Password' onChange={(e) => {
                        setPassword(e.target.value);
                    }}/>
                    <button className='LoginButton'>Login</button>
                </form>

                <div className='Bar'></div>
                <div>Or</div>
                <div className='Bar'></div>
                <div className='CreateAlias'>Create new alias.</div>

                <form className='AliasForm' onSubmit={createUser}> 
                    <input className='AliasInput' required type='text' placeholder='Alias' onChange={(e) => {
                        setAlias(e.target.value);
                    }}/>
                    <input className='AliasPassword' required type='password' placeholder='Password' onChange={(e) => {
                        setPassword(e.target.value);
                    }}/>
                    <button className='LoginButton'>Create</button>
                </form>

                <div className='Bar'></div>
                <div className='CurrentAlias'>Current Alias: <span className='Alias'>{displayAlias}</span></div>
                <div className='SideVersion'>Version:1.1.4</div>
            </div>
            <div className='ChatBox ContainerItem'>
                <div className='MessageBox'>
                    &lt;<span className='Alias'>Peter</span>&gt; {message}
                </div>
                
                <form className='MessageForm' onSubmit={sendMessage}>
                    <input className='MessageInput' required type='text' placeholder='Message' onChange={(e) => {
                        setMessage(e.target.value);
                    }}/>
                    <button className='SendButton'>Send</button>
                </form>

            </div>
        </div>
    );
}

export default Chat;
